import axios from 'axios';

export const register = ({ email, password }) => async dispatch => {
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }
    const body = JSON.stringify({
        email, password
    });

    try {
        const res = await axios.post('/api/users', body, config);

        dispatch({ 
          payload: res.data
        });

      } catch(err) {
        console.error('error')

      }



}
